        {
            (byte) XQuadrant,
            (byte) XOffset,
            (byte) YQuadrant,
            (byte) YOffset,
            (byte) Name.Length
        };
        bytes.AddRange(buffer);
        bytes.AddRange(BitConverter.GetBytes(Id));

        return bytes.ToArray();
    }
