    public IEnumerable<KeyValuePair<HashSet<StoreKey>, T>> GetEnumerator() => _store.Select(selector: kvp =>
        new KeyValuePair<HashSet<StoreKey>, T>(_reverseIndex[kvp.Key], kvp.Value));

    public int Count => _store.Count;

